     function results($id)
    {   
        // check if the patients exists before trying to edit it
        $data['patients'] = $this->Results_model->get_results($id);
        // print_r($data['patients']);exit();

        
        if(isset($data['patients']['id']))
        {
            $this->load->library('form_validation');

            $this->form_validation->set_rules('name','Patient Name','required');
        
            if($this->form_validation->run())     
            {   
                $params = array(
                'title' => $this->input->post('title'),
                'name' => $this->input->post('name'),
                'surname' => $this->input->post('surname'),
                'lab_sub_no' => $this->input->post('lab_sub_no'),
                'gender' => $this->input->post('gender'),
                'tel' => $this->input->post('tel'),
                'email' => $this->input->post('email'),
                'address' => $this->input->post('address'),
                'passport_no' => $this->input->post('passport_no'),
                'dob' => $this->input->post('dob'),
                // 'relevant_info' => $this->input->post('relevant_info'),
                // 'case_define' => '',
                // 'specimen_info' => '',
                'created_by' => $this->session->userdata('first_name'),
                );
                 
              
                $this->Patients_model->update_patients($id,$params);            
                redirect('patients/index');
            }
            else
