    /*
     * Get results by id
     */
    function get_results($id)
    {
      $this->db->select('results.*,patients.name as patient_name,patients.surname as patient_last_name, patients.lab_sub_no as bar_code_no,patients.passport_no as passport,patients.address as patient_address,patients.email as patient_email,patients.date_swab_taken as date_swab_taken_patient,patients.time_swab_taken as time_swab_taken_patient,patients.dob as dob_patients');

        $this->db->from('results');
        $this->db->where('results.id',$id);
        $this->db->join('patients','results.patient_id=patients.id','left outer');
        
             return $this->db->get()->row_array();

    }
     function get_results2($id)
