       $this->db->select('results.*,test_identifiers.test_identifiers as test_identifiers_field');
        $this->db->from('results');
        $this->db->join('test_identifiers','results.lab_sub_no=test_identifiers.id','left outer');
             return $this->db->get()->row_array();
    }

    function login($params)
    {
        return $this->db->get_where('results',$params)->row_array();
    }
        
    /*
     * Get all results
     */
    function get_all_results()
    {
        $this->db->order_by('id', 'desc');
        return $this->db->get('results')->result_array();
    }
